import { WeatherDatas } from 'src/app/modules/models/interfaces/WeatherDatas';
import { WeatherService } from './../../services/weather.service';
import { Component, OnDestroy, OnInit } from '@angular/core';
import { Subject, takeUntil } from 'rxjs';
import { faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons';

@Component({
  selector: 'app-weather-home',
  templateUrl: './weather-home.component.html',
  styleUrls: []
})
export class WeatherHomeComponent implements OnInit, OnDestroy {
  private readonly destroy$: Subject<void> = new Subject();
  initialcityName = 'São Paulo';
  weatherDatas!: WeatherDatas;
  searchIcon = faMagnifyingGlass;

  constructor(private WeatherService: WeatherService) {}

  ngOnInit(): void {
    this.getweatherData(this.initialcityName);
  }

  getweatherData(cityName: string):void{
    this.WeatherService.getWeatherDatas(cityName)
    .pipe(takeUntil(this.destroy$))
    .subscribe({
      next: (response) => {
        response && (this.weatherDatas = response);
        console.log(this.weatherDatas)
      },
      error: (error)=> console.log(error),
    })
  }

  onSubmit(): void{
    this.getweatherData(this.initialcityName);
    this.initialcityName = '';
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }
}
<div class="search">
  <form action="" #form="ngForm" (submit)="onSubmit()">
      <input
        type="text"
        placeholder="Buscar Localização"
        name="city"
        [(ngModel)]="initialcityName"
        />
        <span (click)="onSubmit()">
          <fa-icon
            animation="fade"
            style="--fa-animation-duration: 5s; cursor: pointer;"
            size="lg"
            class="search-icon"
            [icon]="searchIcon"
          ></fa-icon>
        </span>
  </form>
</div>
<app-weather-card [WeatherDatasImput]="weatherDatas"></app-weather-card>
